(function(){const a=document.getElementById("back_to_top");window.onscroll=function(){scrollFunction(a)},a.addEventListener("click",backToTop)})();function scrollFunction(a){const b=document.documentElement.scrollTop||document.body.scrollTop;a&&(a.style.visibility=b>450?"visible":"hidden")}function backToTop(a){a.preventDefault(),document.body.scrollTop=0,document.documentElement.scrollTop=0}